import React from "react";
import Form from "../../components/shared/Form/Form";
import { useSelector } from "react-redux";
import Spinner from "../../components/shared/Spinner";

const Login = () => {
  const { loading, error } = useSelector((state) => state.auth);

  // Show an alert for error (optional)
  if (error) {
    alert(error);
  }

  return (
    <>
      {loading ? (
        <Spinner />
      ) : (
        <div>
          <div
            className="form-container rounded-lg"  // Applying rounded corners here
            style={{
              position: "relative", // Ensure positioning for the pseudo-element
              overflow: "hidden", // Ensures content doesn't overflow the rounded corners
            }}
          >
            {/* Background image with blur */}
            <div
              style={{
                position: "absolute",
                top: 0,
                left: 0,
                right: 0,
                bottom: 0,
                backgroundImage: 'url("./assets/images/banner1.jpg")',
                backgroundSize: "cover",
                backgroundPosition: "center",
                filter: "blur(8px)", // Applying the blur effect to the background
                zIndex: -1, // Make sure the background is behind the content
              }}
            ></div>

            {/* Form content */}
            <div
              style={{
                position: "relative", // Ensures the form content appears above the blurred background
                zIndex: 1,
              }}
            >
              <Form
                formTitle={"Login Page"}
                submitBtn={"Login"}
                formType={"login"}
              />
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Login;
